import React, { useState, useEffect } from "react";
import axios from "axios";
import Menu from "../Menu/Menu";

function ConfigureBudgets() {
  const [category, setCategory] = useState("");
  const [budget, setBudget] = useState("");
  const [budgetList, setBudgetList] = useState([]);
  const [months, setMonths] = useState([
    "January",
    "February",
    "March",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ]);

  const [selectedMonth, setSelectedMonth] = useState("");
  const [userId, setUserId] = useState(localStorage.getItem("userId"));
  const [feedbackMessage, setFeedbackMessage] = useState("");

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const handleBudgetChange = (e) => {
    setBudget(e.target.value);
  };

  const handleAddBudget = async () => {
    if (category && budget && selectedMonth) {
      const token=localStorage.getItem("token")
      try {
       
        const response = await axios.get(`http://159.89.52.202:3002/check-existing-budget/${userId}/${selectedMonth}/${category}`,{
          headers:{
            Authorization:`Bearer ${token}`
          }
        });

        if (response.data.exists) {
          setFeedbackMessage("Budget for this category already exists for the selected month.");
        } else {
          setBudgetList([...budgetList, { category, budget, month: selectedMonth }]);
          setCategory("");
          setBudget("");
          setFeedbackMessage(""); 
        }
      } catch (error) {
        console.error("Error checking existing budget:", error);
        setFeedbackMessage("Error checking existing budget.");
      }
    }
  };

  const handleEditBudget = (index) => {
    const editedBudget = budgetList[index];
    setCategory(editedBudget.category);
    setBudget(editedBudget.budget);

    const updatedBudgetList = [...budgetList];
    updatedBudgetList.splice(index, 1);
    setBudgetList(updatedBudgetList);
  };

  const handleSaveBudgets = async () => {
    try {
      if (budgetList.length === 0 || !selectedMonth) {
        console.error("Month or budget list is empty");
        return;
      }
      const token = localStorage.getItem("token");
      console.log(token);
      console.log(months);
      await axios.post(
        "http://159.89.52.202:3002/configure-budgets",
        {
          userId,
          months: selectedMonth,
          budgetList,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log("Budgets saved successfully!");

      setBudgetList([]);
      setSelectedMonth("");
      setFeedbackMessage("Budgets saved successfully!");
    } catch (error) {
      setBudgetList([]);
      setSelectedMonth("");
      setFeedbackMessage("Error saving budgets: " + error.message);
      console.error("Error saving budgets:", error);
    }
  };

  const handleBack = () => {
    window.history.back();
  };



  return (
    <div>
    <Menu/>
    <div>
      <h2>Configure Budgets</h2>

      <div>
        <label htmlFor="months">Month:</label>
        <select
          id="months"
          onChange={(e) => setSelectedMonth(e.target.value)}
          value={selectedMonth}
          style={{marginBottom:'20px'}}
        >
          <option value="">Select Month</option>
          {months.map((month) => (
            <option key={month} value={month}>
              {month}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label htmlFor="category">Category:</label>
        <input
          type="text"
          id="category"
          value={category}
          onChange={handleCategoryChange}
          style={{marginBottom:'10px'}}
        />
      </div>

      <div>
        <label htmlFor="budget">Budget:</label>
        <input
          type="number"
          id="budget"
          value={budget}
          onChange={handleBudgetChange}
          style={{marginBottom:'10px'}}
        />
      </div>

      <button className="buttonManage" type="button" onClick={handleAddBudget}>
        Add Budget
      </button>

      {feedbackMessage && <p>{feedbackMessage}</p>}
      <ul>
        {budgetList.map((item, index) => (
          <li key={index}>
            {item.category}: {item.budget}
            <button className="buttonManage" type="button" onClick={() => handleEditBudget(index)} >
              Edit
            </button>
          </li>
        ))}
      </ul>
      <button className="buttonManage" type="button" onClick={handleSaveBudgets}  style={{marginRight:'15px'}}>
        Save Budgets
      </button>
      <button className="buttonManage" type="button" onClick={handleBack}>
        Back
      </button>
    </div>
    </div>
  );
}

export default ConfigureBudgets;
